jQuery(function($){
  Lang8.Views.JournalEditView = Backbone.View.extend({
    el: '#journals_edit',

    initialize: function(){

      // initialize intro.js
      if (this.$el.find('#main_container').data('show_tutorial')) {
        introJs().start().onchange(function(element){
          $($.browser.msie || $.browser.firefox || $.browser.opera ? 'html' : 'body').animate({
            scrollTop: $(element).offset().top - 100
          }, 1000);
        });
      }
      if (this.$el.find('#main_container').data('should_correct_more')) {
        this.$el.find('#should_correct_more').modal('show');
      }

      // Show Intro
      this.showIntroForNewFeature();
    },

    events: {
      'click  #post_entry_from_edit' : 'onClickPostEntryFromEdit',
      'click  #title_input'          : 'onClickTitleInput',
      'click  .display_post_options' : 'onClickDisplayPostOptions',
      'change #journal_body_mo'      : 'onChangeJournalBodyMo',
      'click  #confirm_button'       : 'onClickConfirm',
      'change #select_correction_type' : 'onChangeSelectBoxCorrectionType',
      // XXX Why this doesn't work? to @TakashiNakagawa
      // 'load #display_hidden_options' : 'show_intro_for_new_feature',
    },

    onClickPostEntryFromEdit: function(e){
      var $target = $(e.currentTarget);
      $('<input />').attr('type', 'hidden').attr('name', 'publish').attr('value', '1').appendTo('#form');
      $target.attr('value', 'please wait...').attr('disabled', true);
      $target.closest('form').submit();
    },

    onClickTitleInput: function(e){
      this.$el.find('#title').show().end();
      $(e.currentTarget).remove();
      this.$el.find('.journal_edit_helptext').text('The first line will be shown as the title if title is blank');
    },

    onClickDisplayPostOptions: function(e){
      $(e.currentTarget).parent().remove();
      this.$el.find('#post_options').show();
    },

    onChangeJournalBodyMo: function(e){
      this.autoResizeTextarea();
    },


    onChangeSelectBoxCorrectionType: function(e) {
      var $target = $(e.currentTarget);
      if (!$target.data('premium-user')) {
        var messageEscaped = _.escape($target.data('confirm-message'));
        var answer = confirm(messageEscaped);

        if (answer) {
          var win = window.open('/premium/intro', 'intro');
          win.focus();
        }

        $target.val(0);
      }
    },

    onClickConfirm: function(e){
      this.$el.find('#form').submit();
    },

    autoResizeTextarea: function(){
      var textarea = this.$el.find('#journal_body_mo');
      if(textarea.val() === ''){
        textarea.removeClass('journal_body_mo_entered');
      } else {
        textarea.addClass('journal_body_mo_entered');
      }
    },


    showIntroForNewFeature: function(e) {
      if (!$j('#display_hidden_options').data('show_intro_for_correction_type')) {
        return
      }

      var intro = this._getIntroJsForNewFeature();

      // Start intro
      intro.start();

      $intro = $j('.custom_intro .introjs-nextbutton');
      $intro.off()
      $intro.on('click', function() {
        intro.goToSecond();
      });
    },

    _getIntroJsForNewFeature: function(e) {
      var step_array = [], steps = [];

      // intro #1, #2
      step_array.push(document.querySelector('#display_hidden_options'));
      step_array.push(document.querySelector('#correction_type_option'));

      for ( var i = 0, len = step_array.length; i < len; i += 1 ) {
        var element = step_array[i];
        var text = element.getAttribute('data-intro-text');
        steps.push({ element: element, intro: text, tooltipClass: 'custom_intro'});
      }
      
      // Hack intro.js
      var intro = introJs().setOptions({ 'steps': steps });
      var self = this;
      intro.goToSecond = function() {
        var hiddenLink = document.querySelector('.display_post_options');
        self.onClickDisplayPostOptions({ currentTarget: hiddenLink });
        intro.refresh().goToStep(2);
      };

      return intro
    },
  });
});
jQuery(function($){
  Lang8.Views.JournalShowView = Backbone.View.extend({
    el: '#journals_show',

    replyTextAreaMessage: {},

    initialize: function(){
      var likedUserOptions = { autoOpen: false, resizable: false, height: 400, width: 370 };
      this.$el.find('#eval_users_list').dialog(
        _.extend(likedUserOptions, { title: 'People who think this correction is good' })
      );

      this.$el.find('#journal_comment_liked_users_list').dialog(
        _.extend(likedUserOptions, { title: 'People who likes this comment' })
      );

      this.$el.find('#liked_users_list').dialog(
        _.extend(likedUserOptions, { title: 'People who likes this entry' })
      );

      this.$el.find('#form_to_edit_note').dialog({
        title: l8.t('cir'),
        autoOpen: false,
        width:506
      });

      this.$el.find('#eval_comment_form').dialog({
        title: l8.t('cit'),
        autoOpen: false,
        width: 686
      });

      this.$el.find('.correction_field a.more_corrections').closest('ul').find('.correct_hide').hide();
      this.$el.find('.correction_field a.more_corrections.hide_more').hide();

      CommentHighlight.on();

      this.$el.find('#report_spam_entry').modal({
        show: false
      });

      var googlePlusOneScript = document.createElement('script');
      googlePlusOneScript.type = 'text/javascript';
      googlePlusOneScript.async = true;
      googlePlusOneScript.src = 'https://apis.google.com/js/plusone.js';
      var scriptElem = document.getElementsByTagName('script')[0];
      scriptElem.parentNode.insertBefore(googlePlusOneScript, scriptElem);


      // For guide friend request message
      var message = this.$el.find('#comments_and_corrections_field').data('guide_friend_request_message');
      var friend_request_url = this.$el.find('#comments_and_corrections_field').data('friend_request_url')
      var link = $('<a/>', {
        href: friend_request_url,
        text: message
      });
      var message_div = $('<div/>').addClass('alert').css('margin', '10px 0 0 70px').append(link);

      if (message) {
        this.$el.find(location.hash).append(message_div);
      }

      // For facebook like button
      var $fbElement = this.$el.find('#links_for_journal .fb-btn iframe');
      if ($fbElement) {
        $fbElement.attr('src', '//www.facebook.com/plugins/like.php?href=' + $('#links_for_journal').data('canonical_url') + '&send=false&layout=button_count&width=100&show_faces=false&font=arial&colorscheme=light&action=like&height=21');
      }
    },

    events: {
      'click .journal_comment_liked_users' : 'showJournalCommentLikeUsers',
      'click .i_like_this_comment' : 'onClickJournalCommentLikeButton',
      'click .like'                        : 'onClickLikeButton',
      'click .eval_user_links'             : 'onClickEvalUserLinks',
      'click .liked_user_links'            : 'onClickLikedUserLinks',
      'click .edit_note_links'             : 'onClickEditNoteLinks',
      'focus .correction_reply_textarea'   : 'onFocusCorrectionReplyTextArea',
      'blur  .correction_reply_textarea'   : 'onBlurCorrectionReplyTextArea',
      'click #vertical_view'               : 'onClickVerticalView',
      'mouseenter .correct_sentence_title' : 'onMouseEnterCorrectSentenceTitleOrBody',
      'mouseleave .correct_sentence_title' : 'onMouseLeaveCorrectSentenceTitleOrBody',
      'mouseenter .correct_sentence_body'  : 'onMouseEnterCorrectSentenceTitleOrBody',
      'mouseleave .correct_sentence_body'  : 'onMouseLeaveCorrectSentenceTitleOrBody',
      'click .perfect-btn'                 : 'onClickPerfectBtn',
      'click .more_corrections'            : 'onClickMoreCorrections',
      //'click .eval_comment_to_correction'  : 'onClickEvalCommentToCorrection',
      'click #refer_btn'                   : 'onClickReferBtn',
      'click #refer_btn_bottom'            : 'onClickReferBtnBottom',
      'click .edit_apply_button'           : 'onClickEditApplyBtn'
    },

    onClickLikeButton: function(e){
      this.sendLikeRequest($(e.currentTarget));
    },

    onFocusCorrectionReplyTextArea: function(e){
      this.showCorrectionReplyTextArea($(e.currentTarget));
    },

    onBlurCorrectionReplyTextArea: function(e){
      this.hideCorrectionReplyTextArea($(e.currentTarget));
    },

    onClickEvalUserLinks: function(){
      this._showDialog(this.$el.find('#eval_users_list'));
    },

    onClickLikedUserLinks: function(){
      this._showDialog(this.$el.find('#liked_users_list'));
    },

    onClickEditNoteLinks: function(){
      this._showDialog(this.$el.find('#form_to_edit_note'));
    },

    onClickVerticalView: function(){
      this._toggleVerticalView();
    },

    onMouseEnterCorrectSentenceTitleOrBody: function(e){
      this._togglePerfectButton('show', $(e.currentTarget));
    },

    onMouseLeaveCorrectSentenceTitleOrBody: function(e){
      this._togglePerfectButton('hide', $(e.currentTarget));
    },

    onClickPerfectBtn: function(e){
      var $target = $(e.currentTarget);
      if($target.closest().find('.perfect').children().length > 0){
        return false;
      }

      $target.hide();

      var id = $target.data('index');
      $(diarycorrect.mark_as_perfect(id)).show();

      return false
    },

    onClickMoreCorrections: function(e){
      $(e.currentTarget)
        .closest('ul')
          .find('.correct_hide').toggle().end()
          .find('a.more_corrections').toggle().end();
      return false;
    },

    //onClickEvalCommentToCorrection: function(e){
    //  var $target = $(e.currentTarget);
    //  var $comment_field = $target.closest('.comment_field');
    //  var $correction_box = $comment_field.find('.correction_field');
    //  var $correction = $correction_box.find('.corrected').first().clone();
    //  var $quote_field = $comment_field.find('.reply_field .quote_field').first();

    //  $quote_field.find('blockquote ul').first().append($correction);
    //  $quote_field.show();
    //  $comment_field.find('#reply_comment_correction_id').first().attr('value', $target.attr('href'));
    //  $comment_field.find('textarea').first().focus();
    //  return false;
    //},

    onClickReferBtn: function(e){
      this.$el.find("#refer_body_mo").toggle('fast');
    },

    onClickReferBtnBottom: function(e){
      this.$el.find('#refer_body_mo_bottom').toggle('fast');
    },

    onClickEditApplyBtn: function(e){
      var correction_notification_message = this.$el.find('#correction_area').data('correction_notification_message');
      if (correction_notification_message && $(e.currentTarget).closest('div').parent().find('.correction_notification').length == 0) {
        var correction_notification = $('<div />').text(correction_notification_message).addClass('correction_notification');
        $(e.currentTarget).closest('div').next('div.correction_draft').after(correction_notification);
      }
    },

    showCorrectionReplyTextArea: function($target){
      var itemId     = $target.attr('data-item-id');
      var $submitBtn = $('#reply_form_for_' + itemId)
      if($submitBtn.hasClass('unclicked')){
        $submitBtn
          .show()
          .removeClass('unclicked');
        this.replyTextAreaMessage[itemId] = $target.val();
        $target
          .val('')
          .css({height: '42px'});
      }
    },

    hideCorrectionReplyTextArea: function($target){
      var itemId     = $target.attr('data-item-id');
      var $submitBtn = $('#reply_form_for_' + itemId)
      var $replyCommentField = $target.closest('.reply_comment_field');
      var $quoteField  = $replyCommentField.find('.quote_field');

      if($target.val() === ""){
        $quoteField.find('blockquote ul')
          .first().empty().end().end()
          .hide().end();
        $replyCommentField.find('#reply_comment_correction_id').first().attr('value', '');
        $submitBtn
          .hide()
          .addClass('unclicked');
        $target
          .val(this.replyTextAreaMessage[itemId])
          .css({height: '14px'})
      }
    },

    sendLikeRequest: function($target){
      var journalId = $target.attr('data-journal_id');
      $.ajax({
        type: 'POST',
        url: '/journals/like',
        data: { id: journalId },
        beforeSend: function(){
          $target.parent().html('');
        },
        success: function(){
          if ($('.liked_user_links').length) {
            var likedMessage = $('.liked_user_links').parent().html();
            $('.liked_user_links').parent().html('You and ' + likedMessage);
          } else {
            $('.liked_message').children(':first-child').html('You like this entry.');
          }
        }
      });
    },

    onClickJournalCommentLikeButton: function(e) {
      var $target = $(e.currentTarget);
      var journalId = $target.attr('data-journal_id');

      $.ajax({
        type: 'POST',
        url: '/journal_comments/like',
        data: { id: journalId, data: 'test' }
      }).done(function(data, status){
        $target.parent().find('.some_people_likes').remove();
        $target.replaceWith(data);
      });

      return false
    },

    showGoodDialog: function(e){
      var $target = this.$el.find('#eval_users_list');
      return this._showDialog($target);
    },

    showLikeDialog: function(e){
      var $target = this.$el.find('#liked_users_list');
      return this._showDialog($target);
    },

    showJournalCommentLikeUsers: function(e) {
      var $target = $(e.currentTarget);
      var $dialog = $('#journal_comment_liked_users_list');

      jQuery.ajax({
        type: 'GET',
        url: $target.attr('href')
      }).done(function(data, status) {
        $dialog.html(data);
      });

      return this._showDialog($dialog);
    },

    editNoteDialog: function(e){
      var $target = this.$el.find('#form_to_edit_note');
      return this._showDialog($target);
    },

    _toggleVerticalView: function(e){
      var $target = this.$el.find('#vertical_view');
      var journalId = $target.attr('data-journal_id');
      if ($target.val() == l8.t('rfv')) {
        $target.val(l8.t('rfw'));
      } else {
        $target.val(l8.t('rfv'));
      }
      this.$el.find('#body_show_ori').toggleClass('body_show_vertical');
      this.$el.find('#body_show_mo').toggleClass('body_show_mo_vertical');
    },

    _showDialog: function($dialog) {
      if ($dialog.dialog('isOpen')) $dialog.dialog('close');
      $dialog.dialog('open');
      return false;
    },

    _togglePerfectButton: function(mode, $target) {
      if($target.find('.perfect').children().length > 0){
        return false;
      }

      if(mode === 'show' || mode === 'hide'){
        $target.find('.perfect-btn')[mode]()
      }
    },
  });
});
