var CommentHighlight = {
    highlight_color: '#fdffaA5',
    url: location.href,
    on: function() {
        var anchor_id = $j.url(CommentHighlight.url).attr('fragment');
        if(anchor_id == '') return true;

        var element = $j("#" + anchor_id + " > .single_comment_box");
        if (element.size() == 0) {
            element = $j("#" + anchor_id + " > .reply_comment_box");
        }

        if (element) {
            var original_color = element.css('background-color');

            element.css('background-color', CommentHighlight.highlight_color);

            setTimeout( function() {
                CommentHighlight.off(original_color);
            }, (4000));
        }
    },
    off: function(color) {
        var anchor_id = $j.url(CommentHighlight.url).attr('fragment');
        var element = $j("#" + anchor_id + " > .single_comment_box");
        if (element.size() == 0) {
            element = $j("#" + anchor_id + " > .reply_comment_box");
        }

        if (element) {
            element.animate({ backgroundColor: color }, 1000 );
        }
    }
}
